import { getSuggestedQuery } from '@testing-library/react';
import React, { FormEvent, useEffect, useState } from 'react';
import style from '../cssmodules/NavbarMod.module.css'
import { Link } from 'react-router-dom'
const Navbar = () => {

    // const [searchInfo, setSearchInfo] = useState()
    // const [query, setQuery] = useState('');


    // const [result,setResult] = useState<Item[] | undefined>();
   


    // const textChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    //     console.log("Kalla på api query")
    //     const searchstring = event.target.value;
    //     setQuery(searchstring)
    //     console.log(searchstring)
    // };

    // const handleKeyDown = (event: React.KeyboardEvent<HTMLInputElement>) => {
    //     if (event.key === 'Enter') {
    //         setSearchInfo(query)
    //     }
    // }
const [recipesFound, setRecipesFound] = useState([]);
const [recipeSearch, setRecipeSearch] = useState('')
    const searchForRecipes = async (query: string): Promise<any> => {
        try{
            const result = await fetch(`http://localhost:3000/recipes/?search=${query}`)
            return (await result.json()).results;
        }
        catch{
            const result = await fetch(`http://localhost:3000/category/?search=${query}`)
            return (await result.json()).results;

        }
    }
    const search = (event: FormEvent<HTMLFormElement>) => {
            event.preventDefault();
            const form = event.target as HTMLFormElement;
            const input = form.querySelector('#searchText') as HTMLInputElement;
            setRecipeSearch(input.value)
    }

    useEffect(() => {
        (async ()=>{
            const query = encodeURIComponent(recipeSearch);
            if(query){
            const response = await searchForRecipes(query) 
            setRecipesFound(response)    
            }

        })();
    }, [recipeSearch] );
    return (
        <div className={style.navwrapper}>
            <span className={style.hamburger}><hr /> <hr /> <hr /></span>
            <Link to={'/'}> <h1 className={style.heading} >
                Greenylicious
            </h1>
            </Link>
            <form onSubmit={event => search(event)}>
            <input type="text"
            id="searchText"
                className={style.input}
                placeholder="Sök på ett recept"
                // onChange={textChange}
                // onKeyDown={handleKeyDown}
            />
            <button>search</button>
            </form>
        </div>
    )
}
export default Navbar
