import style from "../cssmodules/SingleRecipeMod.module.css";
import Sidebar from "./Sidebar";
import { useParams } from "react-router-dom";
import { useEffect, useState } from "react";
import axios from "axios";
import StarRatings from "./ratings";
import Navbar from "./Navbar";
import { FaStar } from "react-icons/fa";

const SingleRecipe = () => {
  const [recipe, setRecipe] = useState<any>({});
  const { recipeId } = useParams();
  useEffect(() => {
    console.log(recipeId);
    const url = `https://planned-sammy-celinaki.koyeb.app/recipes/${recipeId}`;
    console.log(url);
    axios
      .get(url)
      .then((res) => {
        console.log(res);
        setRecipe(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);
  const [voted, setVote] = useState(false);
  const setClicked = () => {
    setVote(true);
  };

  return (
    <>
      {/* <Navbar /> */}
      <div className={style.bigwrapper}>
        {/* <Sidebar /> */}
        <div className={style.recipewrap}>
          <article className={style.card}>
            {/* <h1>{recipe.title}</h1> */}
            <section className={style.imgdescr}>
              <img src={recipe.imgUrl} alt="" className={style.bigimg} />
              {/* <h2>{recipe.description} </h2> */}
            </section>
            <div className={style.content}>
              <h1>{recipe.title}</h1>
              <span style={{ alignItems: "center", display: "flex" }}>
                <FaStar size={26} color={"rgb(241, 210, 32)"} />
                <span
                  style={{
                    marginLeft: "0.8rem",
                    fontWeight: "bolder",
                    title: "Rating stars",
                  }}
                >
                  4.5
                </span>
              </span>

              <div className={style.instructions}>
                <div className={style.lists}>
                  <h2>Ingredients</h2>
                  <ul className={style.nodec}>
                    {recipe.ingredients &&
                      recipe.ingredients?.map((ingredient: any) => (
                        <li className={style.vegan} key={recipe._id}>
                          &#x1f331;
                          {ingredient.name.charAt(0).toUpperCase() +
                            ingredient.name.slice(1)}{" "}
                          {ingredient.amount} {ingredient.unit}
                        </li>
                      ))}{" "}
                  </ul>
                </div>
                <div className={style.lists}>
                  <h2>Instructions</h2>
                  <ol className={style.ul}>
                    {recipe.instructions &&
                      recipe.instructions?.map((step: any) => (
                        <li key={step.index}>{step}</li>
                      ))}
                  </ol>
                </div>
              </div>
            </div>
          </article>
          <section className={style.rating}>
            {/* <span  {setClicked((clicked) => false &&)} > test</span> */}
            {voted === false ? (
              <>
                <h1>Lägg ett betyg!</h1>{" "}
                <span onClick={setClicked}>
                  {recipe.ratings && (
                    <StarRatings
                      edit={true}
                      recipeId={recipe._id}
                      recipeRatings={recipe.ratings}
                    />
                  )}
                </span>{" "}
              </>
            ) : (
              <h1>Tack för ditt betyg!</h1>
            )}
          </section>
        </div>
      </div>
    </>
  );
};
export default SingleRecipe;
